package corejava;

public class IfElseExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 100, b = 30;

		if (a < b) {
			System.out.println(b+" is bigger");
		} else {
			System.out.println(a+" is bigger");
		}

		System.out.println("outside the if else");

	}
}
